if ((flow.get("MQTTBrokerUsername") == undefined) || (flow.get("MQTTBrokerPassword") == undefined))
    {
    msg.broker = JSON.parse('{"broker": "' + flow.get("MQTTBrokerIP") + '", "port": ' + flow.get("MQTTBrokerPort") + ', "force": true }');
    msg.action = "connect";
    node.send(msg)  
    }
else
    {
    msg.broker = JSON.parse('{"broker": "' + flow.get("MQTTBrokerIP") + '", "port": ' + flow.get("MQTTBrokerPort") + ', "username" : "' + flow.get("MQTTBrokerUsername") + '", "password" : "' + flow.get("MQTTBrokerPassword") + '", "force": true }');
    msg.action = "connect";
    node.send(msg)    
    }

msg.action = "subscribe";
msg.topic = flow.get("StateSetPrefix")+"/#";
node.send(msg)

return null;

msg.payload.keys = Object.keys(msg.payload)
msg.payload.values = Object.values(msg.payload)

for (let i = 0; i < msg.payload.keys.length; i++)
{
    flow.set(msg.payload.keys[i], msg.payload.values[i])
}

var mqttmsg = {}
var daikinmsg = {}
daikinmsg.payload = {}


//Set State variables
if ((msg.topic.includes("daikin")) && (msg.topic.includes("state")))
{
    flow.set(msg.topic, msg.payload)
}


for (let i = 0; i < flow.get("UnitCount"); i++)

{
    var ssid = flow.get("SSIDUnit"+i)
    var unitname = flow.get("NameUnit"+i)

    //POWER 
    if (msg.topic.includes("/switch/power/"+unitname+"/set"))
        {
            mqttmsg.topic = "daikin/switch/power/" + unitname + "/state"

            daikinmsg.topic = "set"
            daikinmsg.payload.managementPoint = "climateControl"
            daikinmsg.payload.dataPoint = "onOffMode"   
            daikinmsg.payload.ssid = ssid

            if (msg.payload == "ON") 
            {
                mqttmsg.payload = "ON"
                daikinmsg.payload.value = "on"

            }
            else if (msg.payload == "OFF") 
            {
                mqttmsg.payload = "OFF"
                daikinmsg.payload.value = "off"
            }
            node.send([[mqttmsg],[null]])
            node.send([[null],[daikinmsg]])
            flow.set("SkipRead", 3)
        }

    //SETPOINT
    else if (msg.topic.includes("/climate/setpoint/"+unitname+"/targetTempCmd"))
        {
            mqttmsg.topic = "daikin/climate/setpoint/" + unitname + "/state"
            mqttmsg.payload = msg.payload

            daikinmsg.topic = "set"
            daikinmsg.payload.managementPoint = "climateControl"
            daikinmsg.payload.dataPoint = "temperatureControl"    
            daikinmsg.payload.ssid = ssid
            //daikinmsg.payload.value = Number(Number(msg.payload.svalue1).toFixed(1))
            //daikinmsg.payload.value = Number(msg.payload).toFixed(1)
            //daikinmsg.payload.value = Number(msg.payload)
            daikinmsg.payload.value = Math.round(Number(msg.payload) / 0.5) * 0.5


            if (flow.get("CurrentModeUnit"+i) == "cooling")
                    {daikinmsg.payload.dataPointPath = '["/operationModes/cooling/setpoints/roomTemperature"]'}
            if (flow.get("CurrentModeUnit"+i) == "heating")
                    {daikinmsg.payload.dataPointPath = '["/operationModes/heating/setpoints/roomTemperature"]'}
            if (flow.get("CurrentModeUnit"+i) == "auto")    
                    {daikinmsg.payload.dataPointPath = '["/operationModes/auto/setpoints/roomTemperature"]'}

            node.send([[mqttmsg],[null]])
            node.send([[null],[daikinmsg]])
            flow.set("SkipRead", 3)
        }

    //MODE
    else if (msg.topic.includes("/select/mode/"+unitname+"/set"))
        {
            if (msg.payload == "off") 
            {
                node.warn("Mode cannot be off, please hide off level in domoticz")
            }
            else
            {
                mqttmsg.topic = "daikin/select/mode/" + unitname + "/state"
                mqttmsg.payload = msg.payload

                daikinmsg.topic = "set"
                daikinmsg.payload.managementPoint = "climateControl"
                daikinmsg.payload.dataPoint = "operationMode"    
                daikinmsg.payload.ssid = ssid
                daikinmsg.payload.value = msg.payload
                flow.set("CurrentModeUnit"+i, msg.payload)
                node.send([[mqttmsg],[null]])
                node.send([[null],[daikinmsg]])
                flow.set("SkipRead", 3)
            }
        }

    //FANDIRECTION
    else if (msg.topic.includes("/select/fandirection/"+unitname+"/set"))
        {
            mqttmsg.topic = "daikin/select/fandirection/" + unitname + "/state"
            mqttmsg.payload = msg.payload

            daikinmsg.topic = "set"
            daikinmsg.payload.managementPoint = "climateControl"
            daikinmsg.payload.dataPoint = "fanControl"    
            daikinmsg.payload.ssid = ssid

            let horizontal = ""
            let vertical = ""

                    if (msg.payload == "Off")
                        {
                        horizontal = "stop"
                        vertical = "stop"
                        }

                    if (msg.payload == "Horizontal")
                        {
                        horizontal = "swing"
                        vertical = "stop"
                        }

                        if (msg.payload == "Vertical")
                        {
                        horizontal = "stop"
                        vertical = "swing"
                        }

                        if (msg.payload == "3D-Swing")
                        {
                        horizontal = "swing"
                        vertical = "swing"
                        }
            daikinmsg.payload.dataPointPath = '/operationModes/' + flow.get("CurrentModeUnit"+i) + '/fanDirection/horizontal/currentMode'
            daikinmsg.payload.value = horizontal
            node.send([[null],[daikinmsg]]);
            daikinmsg.payload.dataPointPath = '/operationModes/' + flow.get("CurrentModeUnit"+i) + '/fanDirection/vertical/currentMode'
            daikinmsg.payload.value = vertical
            node.send([[null],[daikinmsg]]);            
            node.send([[mqttmsg],[null]])
            flow.set("SkipRead", 3)
        }

    //FANSPEED
    else if (msg.topic.includes("/select/fanspeed/"+unitname+"/set"))
        {
            mqttmsg.topic = "daikin/select/fanspeed/" + unitname + "/state"
            mqttmsg.payload = msg.payload

            daikinmsg.topic = "set"
            daikinmsg.payload.managementPoint = "climateControl"
            daikinmsg.payload.dataPoint = "fanControl"    
            daikinmsg.payload.ssid = ssid

                if ((msg.payload.includes("Level")) && (flow.get("CurrentModeUnit"+i) != "dry"))
                {
                daikinmsg.payload.dataPointPath = '/operationModes/' + flow.get("CurrentModeUnit"+i) + '/fanSpeed/currentMode'
                daikinmsg.payload.value = "fixed"
                node.send([[null],[daikinmsg]]);  

                daikinmsg.payload.dataPointPath = '/operationModes/' + flow.get("CurrentModeUnit"+i) + '/fanSpeed/modes/fixed'
                daikinmsg.payload.value = Number(msg.payload.slice(-1))
                node.send([[null],[daikinmsg]]);
                }

                if ((msg.payload == ("Auto")) && (flow.get("CurrentModeUnit"+i) != "dry"))
                {
                daikinmsg.payload.dataPointPath = '/operationModes/' + flow.get("CurrentModeUnit"+i) + '/fanSpeed/currentMode'
                daikinmsg.payload.value = "auto"
                node.send([[null],[daikinmsg]]);
                }

                if ((msg.payload == ("Quiet")) && (flow.get("CurrentModeUnit"+i) != "dry"))
                {
                daikinmsg.payload.dataPointPath = '/operationModes/' + flow.get("CurrentModeUnit"+i) + '/fanSpeed/currentMode'
                daikinmsg.payload.value = "quiet"
                node.send([[null],[daikinmsg]]);
                }

                if (msg.payload == ("Off"))
                {
                node.warn("Fan Speed cannot be off, please hide off level in domoticz")
                }

            node.send([[mqttmsg],[null]])
            flow.set("SkipRead", 3)
        }

    //POWERFUL
    else if (msg.topic.includes("/switch/powerful/"+unitname+"/set"))
        {
            mqttmsg.topic = "daikin/switch/powerful/" + unitname + "/state"


                daikinmsg.topic = "set"
                daikinmsg.payload.managementPoint = "climateControl"
                daikinmsg.payload.dataPoint = "powerfulMode"   
                daikinmsg.payload.ssid = ssid

                if (msg.payload == "ON") 
                {
                    mqttmsg.payload = "ON"
                    daikinmsg.payload.value = "on"

                }
                else if (msg.payload == "OFF") 
                {
                    mqttmsg.payload = "OFF"
                    daikinmsg.payload.value = "off"
                }
                node.send([[mqttmsg],[null]])
                node.send([[null],[daikinmsg]])
                flow.set("SkipRead", 3)
        }

    //ECONO
    else if (msg.topic.includes("/switch/econo/"+unitname+"/set"))
        {
            mqttmsg.topic = "daikin/switch/econo/" + unitname + "/state"


                daikinmsg.topic = "set"
                daikinmsg.payload.managementPoint = "climateControl"
                daikinmsg.payload.dataPoint = "econoMode"   
                daikinmsg.payload.ssid = ssid

                if (msg.payload == "ON") 
                {
                    mqttmsg.payload = "ON"
                    daikinmsg.payload.value = "on"

                }
                else if (msg.payload == "OFF") 
                {
                    mqttmsg.payload = "OFF"
                    daikinmsg.payload.value = "off"
                }
                node.send([[mqttmsg],[null]])
                node.send([[null],[daikinmsg]])
                flow.set("SkipRead", 3)
        }

    //LED 
    if (msg.topic.includes("/switch/led/"+unitname+"/set"))
        {
            mqttmsg.topic = "daikin/switch/led/" + unitname + "/state"

            daikinmsg.topic = "set"
            daikinmsg.payload.managementPoint = "gateway"
            daikinmsg.payload.dataPoint = "ledEnabled"   
            daikinmsg.payload.ssid = ssid

            if (msg.payload == "ON") 
            {
                mqttmsg.payload = "ON"
                daikinmsg.payload.value = true

            }
            else if (msg.payload == "OFF") 
            {
                mqttmsg.payload = "OFF"
                daikinmsg.payload.value = false
            }
            node.send([[mqttmsg],[null]])
            node.send([[null],[daikinmsg]])
            flow.set("SkipRead", 3)
        }

}

return null

//Save all state messages first
const currentminutes = new Date().getMinutes();

    for (var i = 0; i < msg.payload.length; i++)
        {

        var StateMessage = {};
        var unitname = flow.get("NameUnit"+i)

        if ((flow.get("SkipRead") == 0) || (flow.get("SkipRead") == undefined))
        {
            //POWER
            if (msg.payload[i].managementPoints.climateControl.onOffMode.value == "off") 
            {
                StateMessage.payload = "OFF"
                if (flow.get("daikin/switch/power/"+unitname+"/state") != "OFF")
                    {
                        StateMessage.topic = "daikin/switch/power/" + unitname + "/state"
                        StateMessage.payload = "OFF"
                        node.send(StateMessage);
                    }                
            } 
            else 
                {
                    StateMessage.payload = "ON"
                    if (flow.get("daikin/switch/power/"+unitname+"/state") != "ON")
                        {
                            StateMessage.topic = "daikin/switch/power/" + unitname + "/state"
                            StateMessage.payload = "ON"
                            node.send(StateMessage);
                        }
                }


            //SETPOINT    
            let setpoint      
            if ((flow.get("CurrentModeUnit"+i) != "dry") && (flow.get("CurrentModeUnit"+i) != "fanOnly"))
            {
                setpoint = eval('msg.payload[i].managementPoints.climateControl.temperatureControl["/operationModes/' + flow.get("CurrentModeUnit"+i) + '/setpoints/roomTemperature"].value')
            }
            else
            {
                setpoint = msg.payload[i].managementPoints.climateControl.temperatureControl["/operationModes/cooling/setpoints/roomTemperature"].value
            }

            if ((flow.get("daikin/climate/setpoint/"+unitname+"/state") != setpoint.toFixed(2)) || (currentminutes == 30)) //|| ((currentminutes % 5 === 0)))
            {
                        StateMessage.topic = "daikin/climate/setpoint/" + unitname + "/state"
                        StateMessage.payload = setpoint.toFixed(2)
                        node.send(StateMessage);
            }

            //MODE
            if (flow.get("daikin/select/mode/"+unitname+"/state") != msg.payload[i].managementPoints.climateControl.operationMode.value)
            {
                        StateMessage.topic = "daikin/select/mode/" + unitname + "/state"
                        StateMessage.payload =msg.payload[i].managementPoints.climateControl.operationMode.value
                        flow.set("CurrentModeUnit"+i, msg.payload[i].managementPoints.climateControl.operationMode.value)
                        node.send(StateMessage);
            }

            //FANDIRECTION
            //Streepje in 3D-Swing nakijken domoticz
            let horizontal = ""
            let vertical = ""
            let fandirection = ""
            horizontal = eval('msg.payload[i].managementPoints.climateControl.fanControl["/operationModes/' + flow.get("CurrentModeUnit"+i) + '/fanDirection/horizontal/currentMode"].value')
            vertical = eval('msg.payload[i].managementPoints.climateControl.fanControl["/operationModes/' + flow.get("CurrentModeUnit"+i) + '/fanDirection/vertical/currentMode"].value')

            if ((horizontal == "stop") && (vertical == "stop")) {fandirection = "Off"}
            else if ((horizontal == "swing") && (vertical == "stop")) {fandirection = "Horizontal"}
            else if ((horizontal == "stop") && (vertical == "swing")) {fandirection = "Vertical"}
            else if ((horizontal == "swing") && (vertical == "swing")) {fandirection = "3D-Swing"}

            if (flow.get("daikin/select/fandirection/"+unitname+"/state") != fandirection)
            {
                        StateMessage.topic = "daikin/select/fandirection/" + unitname + "/state"
                        StateMessage.payload = fandirection
                        node.send(StateMessage);
            }

            //FANSPEED
            //MAG NIET OFF ZIJN
            let FanSpeedMode = eval('msg.payload[i].managementPoints.climateControl.fanControl["/operationModes/' + flow.get("CurrentModeUnit"+i) + '/fanSpeed/currentMode"].value')
            let FanSpeedFixed = eval('msg.payload[i].managementPoints.climateControl.fanControl["/operationModes/' + flow.get("CurrentModeUnit"+i) + '/fanSpeed/modes/fixed"].value')
            let fanspeed = ""

            if (FanSpeedMode == "fixed") {fanspeed = "Level " + FanSpeedFixed}
            else if (FanSpeedMode == "auto") {fanspeed = "Auto"}
            else if (FanSpeedMode == "quiet") {fanspeed = "Quiet"}   

            if (flow.get("daikin/select/fanspeed/"+unitname+"/state") != fanspeed)
            {
                        StateMessage.topic = "daikin/select/fanspeed/" + unitname + "/state"
                        StateMessage.payload = fanspeed
                        node.send(StateMessage);
            }

            //POWERFUL
            if (msg.payload[i].managementPoints.climateControl.powerfulMode.value == "off") 
            {
                StateMessage.payload = "OFF"
                if (flow.get("daikin/switch/powerful/"+unitname+"/state") != "OFF")
                    {
                        StateMessage.topic = "daikin/switch/powerful/" + unitname + "/state"
                        StateMessage.payload = "OFF"
                        node.send(StateMessage);
                    }                
            } 
            else 
                {
                    StateMessage.payload = "ON"
                    if (flow.get("daikin/switch/powerful/"+unitname+"/state") != "ON")
                        {
                            StateMessage.topic = "daikin/switch/powerful/" + unitname + "/state"
                            StateMessage.payload = "ON"
                            node.send(StateMessage);
                        }
                }

            //ECONO
            if (msg.payload[i].managementPoints.climateControl.econoMode.value == "off") 
            {
                StateMessage.payload = "OFF"
                if (flow.get("daikin/switch/econo/"+unitname+"/state") != "OFF")
                    {
                        StateMessage.topic = "daikin/switch/econo/" + unitname + "/state"
                        StateMessage.payload = "OFF"
                        node.send(StateMessage);
                    }                
            } 
            else 
                {
                    StateMessage.payload = "ON"
                    if (flow.get("daikin/switch/econo/"+unitname+"/state") != "ON")
                        {
                            StateMessage.topic = "daikin/switch/econo/" + unitname + "/state"
                            StateMessage.payload = "ON"
                            node.send(StateMessage);
                        }
                }



            //LED
            if (msg.payload[i].managementPoints.gateway.ledEnabled.value == false) 
            {
                StateMessage.payload = "OFF"
                if (flow.get("daikin/switch/led/"+unitname+"/state") != "OFF")
                    {
                        StateMessage.topic = "daikin/switch/led/" + unitname + "/state"
                        StateMessage.payload = "OFF"
                        node.send(StateMessage);
                    }                
            } 
            else 
                {
                    StateMessage.payload = "ON"
                    if (flow.get("daikin/switch/led/"+unitname+"/state") != "ON")
                        {
                            StateMessage.topic = "daikin/switch/led/" + unitname + "/state"
                            StateMessage.payload = "ON"
                            node.send(StateMessage);
                        }
                }
        } // End of skipread

        //INDOOR TEMPERATURE
        let indoortemp = msg.payload[i].managementPoints.climateControl.sensoryData["/roomTemperature"].value

        if ((flow.get("daikin/sensor/indoor_temp/"+unitname+"/state") != indoortemp) || (currentminutes == 30)) //|| ((currentminutes % 5 === 0)
                {
                    StateMessage.topic = "daikin/sensor/indoor_temp/" + unitname + "/state"
                    StateMessage.payload = indoortemp
                    node.send(StateMessage);
                }  

        //OUTDOOR TEMPERATURE
        let outdoortemp = msg.payload[i].managementPoints.climateControl.sensoryData["/outdoorTemperature"].value

        if ((flow.get("daikin/sensor/outdoor_temp/"+unitname+"/state") != outdoortemp) || (currentminutes == 30)) //|| ((currentminutes % 5 === 0) 
                {
                    StateMessage.topic = "daikin/sensor/outdoor_temp/" + unitname + "/state"
                    StateMessage.payload = outdoortemp
                    node.send(StateMessage);
                }

    }

if (flow.get("SkipRead") > 0)
{
flow.set("SkipRead", (flow.get("SkipRead") - 1))
}

return null;

if ((flow.get("CreatekWhCool") == true) || (flow.get("CreatekWhHeat") == true) || (flow.get("CreatekWhTotal") == true))
{
    const currentminutes = new Date().getMinutes();
    var StateMessage = {}
    var kWhData = {}
    kWhData.payload = ""
    var previouskwhcool
    var previouskwhheat
    var firstrun = false
    var writetofile = false

    for (let i = 0; i < msg.payload.length; i++)
    {

    var CoolKWHarray = []
    var HeatKWHarray = []
    var totalkwhcool
    var totalkwhheat

    CoolKWHarray = msg.payload[i].managementPoints.climateControl.consumptionData["/electrical"].cooling.d
    HeatKWHarray = msg.payload[i].managementPoints.climateControl.consumptionData["/electrical"].heating.d

        var dailykwhcool = CoolKWHarray.reduce(function(a, b){return a + b*10;}, 0);
        var dailykwhheat = HeatKWHarray.reduce(function(a, b){return a + b*10;}, 0);

        if (flow.get(flow.get("NameUnit"+i)+"TotalkWhCool") == undefined) 
        {
            flow.set(flow.get("NameUnit"+i)+"TotalkWhCool", 0)
            totalkwhcool = 0
            firstrun = true
            //First run, set kWh counter to zero.
            StateMessage.topic = "daikin/sensor/kwh_cool/" + flow.get("NameUnit"+i) + "/state"
            StateMessage.payload = "0"
            node.send([[StateMessage], null]) 
        }
        else
        {
            totalkwhcool = flow.get(flow.get("NameUnit"+i)+"TotalkWhCool")
        }

        if (flow.get(flow.get("NameUnit"+i)+"PreviouskWhCool") == undefined)
        {previouskwhcool = 0; flow.set(flow.get("NameUnit"+i)+"PreviouskWhCool", 0)}
        else
        {previouskwhcool = flow.get(flow.get("NameUnit"+i)+"PreviouskWhCool")}


        if (flow.get(flow.get("NameUnit"+i)+"TotalkWhHeat") == undefined) 
        {

            flow.set(flow.get("NameUnit"+i)+"TotalkWhHeat", 0)    
            totalkwhheat = 0
            firstrun = true
            //First run, set kWh counter to zero.
            StateMessage.topic = "daikin/sensor/kwh_heat/" + flow.get("NameUnit"+i) + "/state"
            StateMessage.payload = "0"
            node.send([[StateMessage], null]) 
        }
        else
        {
            totalkwhheat = flow.get(flow.get("NameUnit"+i)+"TotalkWhHeat")
        }

        if (flow.get(flow.get("NameUnit"+i)+"PreviouskWhHeat") == undefined)
        {previouskwhheat = 0; flow.set(flow.get("NameUnit"+i)+"PreviouskWhHeat", 0)}
        else
        {previouskwhheat = flow.get(flow.get("NameUnit"+i)+"PreviouskWhHeat")}

        //Previous can only be bigger if a new day started (so values of previous were deleted from the daikin array)
        if ((previouskwhcool) > (dailykwhcool)) {previouskwhcool = dailykwhcool; flow.set(flow.get("NameUnit"+i)+"PreviouskWhCool", previouskwhcool); writetofile = true}
        if ((previouskwhheat) > (dailykwhheat)) {previouskwhheat = dailykwhheat; flow.set(flow.get("NameUnit"+i)+"PreviouskWhHeat", previouskwhheat); writetofile = true}

        //Send Updated kWh to domoticz
        //Cool
        if ((totalkwhcool + dailykwhcool) > (previouskwhcool + totalkwhcool))
            {
            StateMessage.topic = "daikin/sensor/kwh_cool/" + flow.get("NameUnit"+i) + "/state"
            StateMessage.payload = ((totalkwhcool + (dailykwhcool - previouskwhcool))/10).toFixed(1)
            if (flow.get("CreatekWhCool") == true) {node.send([[StateMessage], null])}
            totalkwhcool = (totalkwhcool + (dailykwhcool - previouskwhcool))
            previouskwhcool= dailykwhcool
            flow.set(flow.get("NameUnit"+i)+"PreviouskWhCool", previouskwhcool)
            flow.set(flow.get("NameUnit"+i)+"TotalkWhCool", totalkwhcool)
            writetofile = true
            }
        //Heat   
        if ((totalkwhheat + dailykwhheat) > (previouskwhheat + totalkwhheat))
            {
            StateMessage.topic = "daikin/sensor/kwh_heat/" + flow.get("NameUnit"+i) + "/state"
            StateMessage.payload = ((totalkwhheat + (dailykwhheat - previouskwhheat))/10).toFixed(1)
            if (flow.get("CreatekWhHeat") == true) {node.send([[StateMessage], null])} 
            totalkwhheat = (totalkwhheat + (dailykwhheat - previouskwhheat))
            previouskwhheat = dailykwhheat
            flow.set(flow.get("NameUnit"+i)+"PreviouskWhHeat", previouskwhheat)
            flow.set(flow.get("NameUnit"+i)+"TotalkWhHeat", totalkwhheat)
            writetofile = true
            }
        //Total    
        if ((totalkwhcool + dailykwhcool + totalkwhheat + dailykwhheat) > (previouskwhcool + totalkwhcool + previouskwhheat + totalkwhheat))
            {
            StateMessage.topic = "daikin/sensor/kwh_total/" + flow.get("NameUnit"+i) + "/state"
            StateMessage.payload = (((totalkwhcool + (dailykwhcool - previouskwhcool)) + (totalkwhheat + (dailykwhheat - previouskwhheat)))/10).toFixed(1)
            if (flow.get("CreatekWhTotal") == true) {node.send([[StateMessage], null])} 
            }

        //Send current kWh to domoticz (avoid sensor timeout)
        if ((currentminutes == 30) && (writetofile == false))
        {
            //Cool
            StateMessage.topic = "daikin/sensor/kwh_cool/" + flow.get("NameUnit"+i) + "/state"
            StateMessage.payload = (flow.get(flow.get("NameUnit"+i)+"TotalkWhCool")/10).toFixed(1) 
            if (flow.get("CreatekWhCool") == true) {node.send([[StateMessage], null])}
            //Heat
            StateMessage.topic = "daikin/sensor/kwh_heat/" + flow.get("NameUnit"+i) + "/state"
            StateMessage.payload = (flow.get(flow.get("NameUnit"+i)+"TotalkWhHeat")/10).toFixed(1) 
            if (flow.get("CreatekWhHeat") == true) {node.send([[StateMessage], null])}      
            //Total
            StateMessage.topic = "daikin/sensor/kwh_total/" + flow.get("NameUnit"+i) + "/state"
            StateMessage.payload = ((flow.get(flow.get("NameUnit"+i)+"TotalkWhCool")/10) + (flow.get(flow.get("NameUnit"+i)+"TotalkWhHeat")/10)).toFixed(1) 
            if (flow.get("CreatekWhTotal") == true) {node.send([[StateMessage], null])}                 
        }

            kWhData.payload= '"'+ flow.get("NameUnit"+i) + 'TotalkWhCool":"' + totalkwhcool.toFixed(1) +'", "' + flow.get("NameUnit"+i) + 'TotalkWhHeat":"' + totalkwhheat.toFixed(1) +'", "' + flow.get("NameUnit"+i) + 'PreviouskWhCool":"' + previouskwhcool.toFixed(1) +'", "' + flow.get("NameUnit"+i) + 'PreviouskWhHeat":"' + previouskwhheat.toFixed(1) +'"'
                if (kWhData.string == undefined)
                    {
                        kWhData.string = "{" + kWhData.payload  
                    }
                else
                    {
                        kWhData.string = kWhData.string  + ", " + kWhData.payload 
                    }
    }

    kWhData.string = kWhData.string + "}"
    kWhData.payload = kWhData.string
    if (writetofile == true)
